ont-family: \'Merriweather\', \'Georgia\', serif;\n        }\n\n        /* Nature Header - Exact Match */\n        .nature-header {\n            background-color: #ffffff;\n            border-bottom: 5px solid #000000;\n            padding: 0;\n        }\n\n        .nature-logo {\n            font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', sans-serif;\n            font-size: 48px;\n            font-weight: 900;\n            color: #000000;\n            margin: 0;\n            padding: 15px 0 15px 96px;\n            letter-spacing: -1px;\n            line-height: 1;\n        }\n\n        .college-name {\n            font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', sans-serif;\n            font-size: 24px;\n            font-weight: 900;\n            color: #000000;\n            margin: 0;\n            padding: 0;\n            letter-spacing: -0.5px;\n            line-height: 1.2;\n        }\n\n        .college-credentials {\n            font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', sans-serif;\n            font-size: 11px;\n            color: #444444;\n            line-height: 1.6;\n            margin: 8px 0 0 0;\n        }\n\n        /* Navigation Bar */\n        .nav-bar {\n            background-color: #ffffff;\n            border-bottom: 3px solid #000000;\n            padding: 18px 96px;\n        }\n\n        .nav-item {\n            font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', sans-serif;\n            font-size: 15px;\n            color: #000000;\n            font-weight: 500;\n            display: inline;\n            margin-right: 35px;\n        }\n\n        /* Breadcrumb */\n        .breadcrumb-section {\n            background-color: #fafafa;\n            padding: 18px 96px;\n            border-bottom: 1px solid #e0e0e0;\n        }\n\n        .breadcrumb {\n            font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', sans-serif;\n            font-size: 13px;\n            color: #666666;\n            margin: 0;\n        }\n\n        .breadcrumb a {\n            color: #226799;\n            text-decoration: none;\n        }\n\n        /* Main Content Container */\n        .content-wrapper {\n            max-width: 1440px;\n            margin: 0 auto;\n            padding: 0;\n            background-color: #ffffff;\n        }\n\n        .content-grid {\n            padding: 40px 96px;\n        }\n\n        /* Left Column - Main Content */\n        .main-content {\n            width: 65%;\n            padding-right: 60px;\n        }\n\n        /* Right Sidebar */\n        .sidebar {\n            width: 35%;\n            padding-left: 30px;\n            border-left: 1px solid #e0e0e0;\n        }\n\n        /* Article Metadata */\n        .article-meta {\n            margin: 10px 0 10px 0;\n        }\n\n        .article-type {\n            font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', sans-serif;\n            font-size: 13px;\n            color: #000000;\n            font-weight: 600;\n            display: inline;\n            margin-right: 10px;\n        }\n\n        .open-access-badge {\n            display: inline;\n            background-color: #ffffff;\n            border: 1px solid #c77700;\n            color: #c77700;\n            font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', sans-serif;\n            font-size: 10px;\n            font-weight: 600;\n            padding: 4px 7px;\n            margin-right: 8px;\n            text-transform: capitalize;\n            border-radius: 4px;\n        }\n\n        .published-date {\n            font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', sans-serif;\n            font-size: 11px;\n            color: #666666;\n            display: inline;\n        }\n\n        /* Article Title */\n        .article-title {\n            font-family: \'Merriweather\', \'Georgia\', serif;\n            font-size: 1.875rem;\n            font-weight: 700;\n            color: #000000;\n            margin: 1.2rem 0 28px 0;\n            line-height: 1.2;\n            letter-spacing: -0.5px;\n        }\n\n        /* Authors */\n        .authors {\n            margin: 0 0 20px 0;\n        }\n\n        .author-name {\n            font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', sans-serif;\n            font-size: 15px;\n            color: #226799;\n            text-decoration: none;\n            font-weight: 600;\n        }\n\n        .author-separator {\n            color: #000000;\n            margin: 0 5px;\n            font-weight: 600;\n        }\n\n        /* Journal Citation */\n        .journal-citation {\n            font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', sans-serif;\n            font-size: 14px;\n            color: #000000;\n            margin: 0 0 35px 0;\n        }\n\n        .journal-name {\n            font-style: italic;\n            color: #226799;\n            text-decoration: none;\n        }\n\n        .cite-link {\n            color: #226799;\n            text-decoration: none;\n            margin-left: 15px;\n        }\n\n        /* Section Headings */\n        .section-heading {\n            font-family: \'Merriweather\', \'Georgia\', serif;\n            font-size: 24px;\n            font-weight: 700;\n            color: #000000;\n            margin: 45px 0 25px 0;\n            line-height: 1.3;\n        }\n\n        /* Body Text - Nature Exact Style */\n        .body-text {\n            font-family: \'Merriweather\', \'Georgia\', serif;\n            font-size: 18px;\n            color: #222222;\n            line-height: 1.75;\n            margin: 0 0 22px 0;\n            font-weight: 400;\n        }\n\n        .body-text sup {\n            font-size: 12px;\n            vertical-align: super;\n            line-height: 0;\n        }\n\n        .body-text a {\n            color: #226799;\n            text-decoration: none;\n        }\n\n        /* Sidebar Elements */\n        .sidebar-download-btn {\n            background-color: #226799;\n            color: #ffffff !important;\n            font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', sans-serif;\n            font-size: 15px;\n            font-weight: 600;\n            text-decoration: none;\n            display: block;\n            padding: 14px 20px;\n            text-align: center;\n            margin: 0 0 30px 0;\n            border: 1.5px solid #226799;\n        }\n\n        .sidebar-download-btn:hover {\n            background-color: #ffffff;\n            color: #226799 !important;\n        }\n\n\n        .sidebar-section-title {\n            font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', sans-serif;\n            font-size: 16px;\n            font-weight: 700;\n            color: #000000;\n            margin: 0 0 15px 0;\n            padding-bottom: 12px;\n            border-bottom: 2px solid #e0e0e0;\n        }\n\n        .sidebar-tab {\n            font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', sans-serif;\n            font-size: 14px;\n            font-weight: 600;\n            color: #000000;\n            display: inline-block;\n            margin-right: 20px;\n            padding-bottom: 10px;\n            border-bottom: 3px solid #000000;\n        }\n\n        .sidebar-link {\n            font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', sans-serif;\n            font-size: 14px;\n            color: #145482;\n            text-decoration: none;\n            display: block;\n            font-weight: 500;\n            margin: 0 0 12px 0;\n            line-height: 1.5;\n        }\n\n        .sidebar-link:hover {\n            text-decoration: underline;\n        }\n\n        /* Footer */\n        .footer-wrapper {\n            background-color: #0d324b;\n            border-top: 1px solid #e0e0e0;\n            padding: 45px 96px;\n        }\n\n        .footer-content {\n            max-width: 1440px;\n            margin: 0 auto;\n        }\n\n        .footer-section-heading {\n            font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', sans-serif;\n            font-size: 12px;\n            font-weight: 700;\n            color: #ffffff;\n            margin: 0 0 15px 0;\n            letter-spacing: 0.5px;\n            text-transform: uppercase;\n        }\n\n        .footer-text {\n            font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', sans-serif;\n            font-size: 13px;\n            color: #ffffff;\n            line-height: 1.7;\n            margin: 0 0 10px 0;\n        }\n\n        .footer-link {\n            color: white !important;\n            text-decoration: none;\n        }\n\n        .footer-divider {\n            height: 1px;\n            background-color: #eaeaea;\n            margin: 35px 0;\n        }\n\n        /* Mobile-specific visibility classes */\n        .mobile-only {\n            display: none !important;\n        }\n\n        .desktop-only {\n            /* display: block !important; */\n        }\n\n        /* Responsive Design */\n        @media only screen and (max-width: 1024px) {\n            .nature-logo {\n                padding-left: 40px !important;\n            }\n\n            .nav-bar {\n                padding: 18px 40px !important;\n            }\n\n            .breadcrumb-section {\n                padding: 18px 40px !important;\n            }\n\n            .content-grid {\n                padding: 40px 40px !important;\n            }\n\n            .footer-wrapper {\n                padding: 45px 40px !important;\n            }\n\n            .main-content {\n                width: 60% !important;\n                padding-right: 40px !important;\n            }\n\n            .sidebar {\n                width: 40% !important;\n            }\n        }\n\n        @media only screen and (max-width: 768px) {\n            .nature-logo {\n                font-size: 36px !important;\n                padding: 12px 0 12px 25px !important;\n            }\n\n            .college-name {\n                font-size: 18px !important;\n            }\n\n            .college-credentials {\n                font-size: 9px !important;\n                line-height: 1.5 !important;\n            }\n\n            .open-access-badge {\n                font-size: 9px !important;\n                padding: 3px 6px !important;\n            }\n\n            .published-date {\n                font-size: 9px !important;\n            }\n\n            .nav-bar {\n                padding: 15px 25px !important;\n            }\n\n            .nav-item {\n                font-size: 13px !important;\n                margin-right: 20px !important;\n            }\n\n            .breadcrumb-section {\n                padding: 15px 25px !important;\n            }\n\n            .content-grid {\n                display: block !important;\n                padding: 0px 25px !important;\n            }\n\n            .main-content {\n                width: 100% !important;\n                padding-right: 0 !important;\n                margin-bottom: 40px !important;\n            }\n\n            .sidebar {\n                width: 100% !important;\n                padding-left: 0 !important;\n                border-left: none !important;\n                border-top: 1px solid #e0e0e0 !important;\n                padding-top: 30px !important;\n            }\n\n            .article-title {\n                font-size: 30px !important;\n            }\n\n            .body-text {\n                font-size: 16px !important;\n            }\n\n            .footer-wrapper {\n                padding: 35px 25px !important;\n            }\n\n            /* Mobile layout reordering */\n            .mobile-only {\n                display: block !important;\n            }\n\n            .desktop-only {\n                display: none !important;\n            }\n\n            /* Mobile button styling */\n            .mobile-only .sidebar-download-btn {\n                margin: 20px 0 3px 0 !important;\n            }\n\n            /* Mobile sections styling */\n            .sidebar {\n                order: 2 !important;\n            }\n        }\n\n        @media only screen and (max-width: 480px) {\n            .nature-logo {\n                font-size: 32px !important;\n                padding: 10px 0 10px 20px !important;\n            }\n\n            .college-name {\n                font-size: 16px !important;\n            }\n\n            .college-credentials {\n                font-size: 8px !important;\n                line-height: 1.4 !important;\n            }\n\n            .open-access-badge {\n                font-size: 8px !important;\n                padding: 3px 5px !important;\n            }\n\n            .published-date {\n                font-size: 8px !important;\n            }\n\n            .nav-item {\n                display: block !important;\n                margin-bottom: 10px !important;\n            }\n\n            .article-title {\n                font-size: 1.2rem !important;\n            }\n\n            .section-heading {\n                font-size: 1.1rem !important;\n            }\n\n            .body-text {\n                font-size: 15px !important;\n            }\n\n            .open-access-badge {\n                display: block !important;\n                margin: 10px 0 !important;\n            }\n        }\n    </style>\n</head>\n\n<body style=\"margin: 0; padding: 0; background-color: #ffffff;\">\n\n    <table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"100%\" id=\"bodyTable\">\n        <tr>\n            <td>\n\n                <!-- Nature Header -->\n                <table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"100%\" class=\"nature-header\">\n                    <tr>\n                        <td>\n                            <table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" style=\"padding: 15px 0 15px 96px;\">\n                   