-- --------------------------------------------------------

--
-- Table structure for table `users`
--

CREATE TABLE `users` (
  `id` int(11) UNSIGNED NOT NULL,
  `user_uuid` varchar(36) NOT NULL COMMENT 'Unique identifier for file storage',
  `email` varchar(255) NOT NULL,
  `full_name` varchar(255) DEFAULT NULL,
  `is_active` tinyint(1) DEFAULT 1,
  `is_admin` tinyint(1) DEFAULT 0,
  `created_at` timestamp NOT NULL DEFAULT current_timestamp(),
  `updated_at` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp()
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

-- --------------------------------------------------------

--
-- Table structure for table `user_attachment_access`
--

CREATE TABLE `user_attachment_access` (
  `id` bigint(20) UNSIGNED NOT NULL,
  `user_id` int(11) UNSIGNED NOT NULL,
  `attachment_id` bigint(20) UNSIGNED NOT NULL,
  `email_id` bigint(20) UNSIGNED NOT NULL COMMENT 'Email through which user accessed this attachment',
  `display_filename` varchar(500) NOT NULL COMMENT 'Filename shown to user (can be customized)',
  `is_deleted` tinyint(1) DEFAULT 0 COMMENT 'User has deleted this attachment reference',
  `deleted_at` datetime DEFAULT NULL,
  `created_at` timestamp NOT NULL DEFAULT current_timestamp()
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Triggers `user_attachment_access`
--
DELIMITER $$
CREATE TRIGGER `trg_attachment_reference_decrement` AFTER UPDATE ON `user_attachment_access` FOR EACH ROW BEGIN
  IF OLD.is_deleted = 0 AND NEW.is_deleted = 1 THEN
    UPDATE `attachments` 
    SET `reference_count` = `reference_count` - 1 
    WHERE `id` = NEW.attachment_id;
    
    -- Queue for deletion if reference count reaches 0
    IF (SELECT `reference_count` FROM `attachments` WHERE `id` = NEW.attachment_id) = 0 THEN
      INSERT INTO `deletion_queue` 
        (`item_type`, `item_id`, `file_path`, `scheduled_for`) 
      SELECT 
        'attachment', 
        NEW.attachment_id, 
        `storage_path`,
        DATE_ADD(NOW(), INTERVAL 7 DAY)  -- Schedule deletion 7 days from now
      FROM `attachments` 
      WHERE `id` = NEW.attachment_id;
    END IF;
  END IF;
END
$$
DELIMITER ;
DELIMITER $$
CREATE TRIGGER `trg_attachment_reference_increment` AFTER INSERT ON `user_attachment_access` FOR EACH ROW BEGIN
  IF NEW.is_deleted = 0 THEN
    UPDATE `attachments` 
    SET `reference_count` = `reference_count` + 1 
    WHERE `id` = NEW.attachment_id;
  END IF;
END
$$
DELIMITER ;

