tn {\n            background-color: #226799;\n            color: #ffffff !important;\n            font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', sans-serif;\n            font-size: 15px;\n            font-weight: 600;\n            text-decoration: none;\n            display: block;\n            padding: 14px 20px;\n            text-align: center;\n            margin: 0 0 30px 0;\n            border: 1.5px solid #226799;\n        }\n\n        .sidebar-download-btn:hover {\n            background-color: #ffffff;\n            color: #226799 !important;\n        }\n\n\n        .sidebar-section-title {\n            font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', sans-serif;\n            font-size: 16px;\n            font-weight: 700;\n            color: #000000;\n            margin: 0 0 15px 0;\n            padding-bottom: 12px;\n            border-bottom: 2px solid #e0e0e0;\n        }\n\n        .sidebar-tab {\n            font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', sans-serif;\n            font-size: 14px;\n            font-weight: 600;\n            color: #000000;\n            display: inline-block;\n            margin-right: 20px;\n            padding-bottom: 10px;\n            border-bottom: 3px solid #000000;\n        }\n\n        .sidebar-link {\n            font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', sans-serif;\n            font-size: 14px;\n            color: #145482;\n            text-decoration: none;\n            display: block;\n            font-weight: 500;\n            margin: 0 0 12px 0;\n            line-height: 1.5;\n        }\n\n        .sidebar-link:hover {\n            text-decoration: underline;\n        }\n\n        /* Footer */\n        .footer-wrapper {\n            background-color: #0d324b;\n            border-top: 1px solid #e0e0e0;\n            padding: 45px 96px;\n        }\n\n        .footer-content {\n            max-width: 1440px;\n            margin: 0 auto;\n        }\n\n        .footer-section-heading {\n            font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', sans-serif;\n            font-size: 12px;\n            font-weight: 700;\n            color: #ffffff;\n            margin: 0 0 15px 0;\n            letter-spacing: 0.5px;\n            text-transform: uppercase;\n        }\n\n        .footer-text {\n            font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', sans-serif;\n            font-size: 13px;\n            color: #ffffff;\n            line-height: 1.7;\n            margin: 0 0 10px 0;\n        }\n\n        .footer-link {\n            color: white !important;\n            text-decoration: none;\n        }\n\n        .footer-divider {\n            height: 1px;\n            background-color: #eaeaea;\n            margin: 35px 0;\n        }\n\n        /* Mobile-specific visibility classes */\n        .mobile-only {\n            display: none !important;\n        }\n\n        .desktop-only {\n            /* display: block !important; */\n        }\n\n        /* Responsive Design */\n        @media only screen and (max-width: 1024px) {\n            .nature-logo {\n                padding-left: 40px !important;\n            }\n\n            .nav-bar {\n                padding: 18px 40px !important;\n            }\n\n            .breadcrumb-section {\n                padding: 18px 40px !important;\n            }\n\n            .content-grid {\n                padding: 40px 40px !important;\n            }\n\n            .footer-wrapper {\n                padding: 45px 40px !important;\n            }\n\n            .main-content {\n                width: 60% !important;\n                padding-right: 40px !important;\n            }\n\n            .sidebar {\n                width: 40% !important;\n            }\n        }\n\n        @media only screen and (max-width: 768px) {\n            .nature-logo {\n                font-size: 36px !important;\n                padding: 12px 0 12px 25px !importan